var localeMap = {
    "langEn": "en_NT",
    "langAm": "am_ET",
    "langOro": "oro_ET"
};

var selectedKey = this.view.lstLang.selectedKey;
var locale = localeMap[selectedKey];

if (locale) {
    kony.i18n.setCurrentLocaleAsync(
        locale,
        function() {
            var currentForm = kony.application.getCurrentForm();
            var navigation = new kony.mvc.Navigation(currentForm.id);
            navigation.navigate();
        },
        function(error) {
            alert("Failed to change language: " + JSON.stringify(error));
        }
    );
} else {
    alert("Selected language is not supported.");
}
