if (!this.fullData) {
  this.fullData = this.view.txnSegement.data;
}

var searchText = this.view.searchBox.text.toLowerCase();

if (searchText.trim() === "") {
  this.view.txnSegement.setData(this.fullData);
} else {
  var filteredData = this.fullData.filter(function(item) {
    return item.txnTitleLabel.toLowerCase().includes(searchText) ||
           item.amount.includes(searchText);
  });
  this.view.txnSegement.setData(filteredData);
}
